    @Test
    public void crawlerMidjourney() {

        // 禁用SSL验证
        SslCertificateUtil.forbiddenCheckSslCertificate();

        Scanner scan = new Scanner(System.in);
        String html = scan.nextLine();
        while(!html.equals("strive_end")) {
            try {

                Document document = Jsoup.parse(html);
                Elements elements = document.getElementsByTag("a");
                if (elements == null || elements.size() == 0) {
                    System.out.println(">>>>>> element is empty");
                    return;
                }
                List<String> hrefList = elements.stream().map(p -> p.attr("href")).collect(Collectors.toList());
                System.out.println(hrefList);

                // 拼接文件名
                String dateName = DateUtil.dateFormatString(new Date(), DateUtil.FORMAT_TIME_SECOND_UNDERLINE);
                String fileName = "";

                // 模拟路径信息
                String href = hrefList.get(0);
                // 先找到最后一个/，然后找到最后一个下划线，存在就以这个命名，不存在，那就取随机uuid
                // eg: https://cdn.discordapp.com/attachments/1054958023698825266/1087501818210299955/cicely_Semi-realistic_Anime_Style_by_Charlie_Bowater_full-body__a23d8e37-fc0e-4ac5-98b7-f8b5717c8a1e.png
                int index_1 = href.lastIndexOf("/");
                if (index_1 == -1) {
                    fileName += RandomStrUtil.createRandomStrWithPrefix(dateName, 36);
                } else {
                    String subStr = href.substring(index_1 + 1);
                    int index_2 = subStr.lastIndexOf("_");
                    if (index_2 == -1) {
                        fileName += RandomStrUtil.createRandomStrWithPrefix(dateName, 36);
                    } else {
                        fileName = subStr.substring(0, index_2);
                    }
                }


                String filePath = "H:\\000000plan\\jpg\\Midjourney" + SystemUtil.getFileSeparator() + fileName;
                if (new File(fileName).exists()) {
                    System.out.println("====== file = " + filePath + ", exist!!!");
                    return;
                }

                List<CompletableFuture<?>> futureList = Lists.newArrayList();
                AtomicInteger count = new AtomicInteger();
                hrefList.forEach(url -> {
                    futureList.add(
                            TaskUtil.runAsync(() ->
                                    CrawlerImgUtil.downloadImgByUrl(url,
                                            filePath + SystemUtil.getFileSeparator() + (StringConstantUtil.preFillZero(count.incrementAndGet(), 4) + ".jpg") + StringConstantUtil.DOT
                                            , CHROME_REQ_HEAD_USER_AGENT)
                            )
                    );
                });
                TaskUtil.waitAnyException(futureList);

            } catch (Exception e) {
                System.err.println("execute crawlerMjdjourney exception = " + e.getLocalizedMessage() + "\n");
            } finally {
                html = scan.nextLine();
            }
        }

    }




